var entTemp = require("../../lib/entityTemplate").entityTemplate;
var util = require("util");
var self = {};

var eventType = function(schema)
{
  eventType.super_.call(this);

  this.moduleName = "Entity eventType";
  this.entityName = "eventType";
  this.schemaName = schema;
  this.tableName = "event_type";

  this.entityId = "id";
  this.entityIdType = this.crudUtils.INTEGER;
}

/*
 * Inheritance.
 */
util.inherits(eventType,entTemp);

eventType.prototype.init = function(opt)
{
  self = this;
  eventType.super_.prototype.init.call(self,opt);
}

eventType.prototype.update = function(opt,callback)
{
  callback({message:"Update not allowed"},null);
}

eventType.prototype.delete = function(opt,callback)
{
  callback({message:"Delete not allowed"},null);
}

eventType.prototype.logicDelete = function(opt,callback)
{
  callback({message:"Logical Delete not allowed"},null);
}

/*
 * Exports.
 */
exports.eventType = eventType;
