/*
 * Check if a javascript object is empty.
 */
exports.isEmptyObject = function(obj)
{
  for (var key in obj)
  {
    if (Object.prototype.hasOwnProperty.call(obj, key))
      return false;
  }

  return true;
}

/*
 * Transform bbox string "BOX(xmin ymin,xmax ymax)" in array of points.
 */
exports.bboxToArray = function(bbox)
{
  if (!bbox)
    return null;

  /* Transform */
  var aPoint = [];

  var strCoord = bbox.substr(4,bbox.length-5);
  if (strCoord)
  {
    var aCoord = strCoord.split(",");

    for (var i = 0;i < aCoord.length;i++)
    {
      var aXY = aCoord[i].split(" ");

      for (var j = 0;j < aXY.length;j++)
        aPoint.push(aXY[j] * 1);
    }
  }

  return aPoint;
}
